def increment_char(c, k):
 return (c + k) % 256
def encrypt(m, k):
 return "§ix: " + "".join(['§ix: ' if c == '\n' else chr(increment_char(ord(c), k)) for c in m])
def decrypt(m, k):
 return "".join(['\n' if m[i:i+5] == '§ix: ' else chr(increment_char(ord(m[i]), -k)) for i in range(len(m)) if m[i] != '§'])
m = input("Enter the message: ")
